import React, { useState } from 'react';
import './Moon.css';
import { planets, moons } from '../../constats'

const MoonList = () => {
    const [count, setCount] = useState();
    const planetsWithMoons = planets.map(planet => {
        const matchedMoons = moons.filter(moon => moon.planetId === planet.id);
        return {
          ...planet,
          moons: matchedMoons
        };
      });
    console.log(planetsWithMoons);
    const handleClickOnMoon = (count: any) => {
        setCount(count + 1);
    }
    return (
        <div className='block'>
            {planetsWithMoons.map((planet) => (
                <div key={planet.id} className='planetBlock'>
                    <div className='planet'>
                        {planet.title}
                        <span>{count}</span>
                    </div>
                    {
                        planet.moons.map((moon) => (
                            <div
                                className='moon'
                                onClick={handleClickOnMoon}
                                key={moon.id}>
                                    {moon.title}
                            </div>
                        ))
                    }
                </div>
            ))}
        </div>
    )
}

export default MoonList;
