import React, { useState } from 'react';
import './Moon.css';
import { planets, moons } from '../../constats'
import Moon from '../Moon/Moon';

const MoonList = () => {
    const [count, setCount] = useState();
    const planetsWithMoons = planets.map(planet => {
        const matchedMoons = moons.filter(moon => moon.planetId === planet.id);
        return {
          ...planet,
          moons: matchedMoons
        };
      });
    console.log(planetsWithMoons);
    const handleClickOnMoon = (count: any) => {
        setCount(count + 1);
    }
    return (
        <div className='block'>
            {planetsWithMoons.map((planet) => (
                <Moon key={planet.id} id={planet.id} title={planet.title} moons={planet.moons} />
            ))}
        </div>
    )
}

export default MoonList;
