import './Moon.css';
import { planets, moons } from '../../constats'

const Moon = () => {
    const planetsWithMoons = planets.map(planet => {
        const matchedMoons = moons.filter(moon => moon.planetId === planet.id);
        return {
          ...planet,
          moons: matchedMoons
        };
      });

      console.log(planetsWithMoons);
