import React from 'react';
import './Moon.css';
import { planets, moons } from '../../constats'

const Moon = () => {
    const planetsWithMoons = planets.map(planet => {
        const matchedMoons = moons.filter(moon => moon.planetId === planet.id);
        return {
          ...planet,
          moons: matchedMoons
        };
      });

      console.log(planetsWithMoons);
    return (
        <div className='block'>
            {planetsWithMoons.map((planet) => (
                <div className='planetBlock'>
                    <div className='planet' key={planet.id}>
                        {planet.title}
                    {
                        planet.moons.map((moon) => (
                            <div className='moon' key={moon.id}>{moon.title}</div>
                        ))
                    }
                </div>
                </div>
            ))}
        </div>
    )
}

export default Moon;
