import React, { useState } from 'react';
import './Moon.css';

interface Planet {
    id: number,
    title: string,
    moons: Moon[],
}

interface Moon {
    id: number,
    planetId: number,
    title: string,
}

interface MoonCount {
    id: number,
    title: string
}

const Moon: React.FC<Planet> = ({
    title,
    moons,
}) => {
    const [count, setCount] = useState(0);
    const [moonsCount, setMoonsCount] = useState<MoonCount[]>([]);
    const handleClickOnMoon = (event: any) => {
        console.log(event.target.id)
        console.log(event.target.textContent)
        const arrayMoonId = moonsCount.filter((id) => id === event.target.id)
        console.log('arrayMoonId', arrayMoonId);
        setMoonsCount(arrayMoonId)
    }
    return (
        <div className='planetBlock'>
            <div className='planet'>
                {title}
                <span>{moonsCount.length || ''}</span>
            </div>
            {
                moons.map((moon) => (
                    <div
                        className='moon'
                        id={moon.id.toString()}
                        onClick={handleClickOnMoon}
                        key={moon.id}>
                            {moon.title}
                    </div>
                ))
            }
        </div>
    )
}

export default Moon;
