import React, { useState } from 'react';
import './Moon.css';
import { planets, moons } from '../../constats'

interface Planet {
    id: number;
    title: string;
    count?: number;
  }

const Moon: React.FC = () => {
    const [isPlanets, setIsPlanets] = useState<Planet[]>(planets);
    const planetsWithMoons = planets.map(planet => {
        const matchedMoons = moons.filter(moon => moon.planetId === planet.id);
        return {
          ...planet,
          moons: matchedMoons
        };
      });

      const handleClickOnMoon = (planetId: number, moonId: number) => {
        setIsPlanets(prevPlanets => {
          return prevPlanets.map(planet => {
            if (planet.id === planetId) {
              const updatedMoons = planet.moons.map(moon => {
                if (moon.id === moonId) {
                  const count = moon.count ? moon.count + 1 : 1;
                  return { ...moon, count };
                }
                return moon;
              });

              return { ...planet, moons: updatedMoons };
            }
            return planet;
          });
        });
      };
    return (
        <>
        {/* <div className='block'>
            {planetsWithMoons.map((planet) => (
                <div key={planet.id} className='planetBlock'>
                    <div className='planet'>
                        {planet.title}
                        <span>{planet.count || null}</span>
                    </div>
                    {
                        planet.moons.map((moon) => (
                            <div
                                className='moon'
                                onClick={() => handleClickOnMoon(planet.id, moon.id)}
                                key={moon.id}>
                                    {moon.title}
                            </div>
                        ))
                    }
                </div>
            ))}
        </div> */}
        <div className='block'>
        {isPlanets.map(planet => (
            <div key={planet.id} className='planetBlock'>
            <div className='planet'>
                {planet.title}
                <span>{planet.count || ''}</span>
            </div>
            {moons
                .filter(moon => moon.planetId === planet.id)
                .map(moon => (
                <div
                    className='moon'
                    onClick={() => handleClickOnMoon(planet.id, moon.id)}
                    key={moon.id}
                >
                    {moon.title}
                </div>
                ))}
            </div>
        ))}
        </div>
        </>
    )
}

export default Moon;
