import React, { useState } from 'react';
import './Moon.css';

interface Planet {
    id: number,
    title: string,
    moons: Moon[],
}

interface Moon {
    id: number,
    planetId: number,
    title: string,
}

const Moon: React.FC<Planet> = ({
    title,
    moons,
}) => {
    const [count, setCount] = useState(0);
    const handleClickOnMoon = () => {
        console.log(count);
        setCount(count + 1);
    }
    return (
        <div className='planetBlock'>
            <div className='planet'>
                {title}
                <span>{count || ''}</span>
            </div>
            {
                moons.map((moon) => (
                    <div
                        className='moon'
                        onClick={handleClickOnMoon}
                        key={moon.id}>
                            {moon.title}
                    </div>
                ))
            }
        </div>
    )
}

export default Moon;
