import React, { useRef, useState } from 'react';
import cn from 'classnames';

import './Moon.css';

interface Planet {
    id: number,
    title: string,
    moons: Moon[],
}

interface Moon {
    id: number,
    planetId: number,
    title: string,
}

interface MoonCount {
    id: number,
    title: string
}

const Moon: React.FC<Planet> = ({
    title,
    moons,
}) => {
    const [moonsCount, setMoonsCount] = useState<MoonCount[]>([]);
    const refPlanet = useRef<HTMLDivElement>(null);
    const toggleMoon = (itemId: number) => {
        const itemIndex = moonsCount.findIndex(item => item.id === itemId);

        if (itemIndex !== -1) {
          // Item exists, remove it from the array
          setMoonsCount(prevItems => prevItems.filter(item => item.id !== itemId));
        } else {
          // Item doesn't exist, add it to the array
          const newItem: MoonCount = { id: itemId, title: `Item ${itemId}` };
          setMoonsCount(prevItems => [...prevItems, newItem]);
        }
      };
      console.dir(refPlanet.current?.getBoundingClientRect());
    return (
        <div className='planetBlock'>
            <div ref={refPlanet} className='planet'>
                {moonsCount.map((moon, index) => (
                    <div className='circle' id={index.toString()} key={index}></div>
                ))}
                {title}
                <span>{moonsCount.length || ''}</span>
            </div>
            {
                moons.map((moon) => (
                    <div
                        className='moon'
                        onClick={() => toggleMoon(moon.id)}
                        key={moon.id}>
                            {moon.title}
                    </div>
                ))
            }
        </div>
    )
}

export default Moon;
